    var numberSafe = 0;
    foreach (var item in listeInfos)
    {
        var isSafe = CheckIfListIsSafe(item);
        var posRemoved = 0;
        while (!isSafe && posRemoved < item.Count)
        {
            var newListWithRemoved = new List<int>(item);
            newListWithRemoved.RemoveAt(posRemoved);
            isSafe = CheckIfListIsSafe(newListWithRemoved);

            if (!isSafe)
            {
                posRemoved++;
            }
        }

        numberSafe += isSafe ? 1 : 0;
    }

    return numberSafe;
}

bool CheckIfListIsSafe(List<int> liste)
{
    if (liste.OrderBy(x => x).ToList().SequenceEqual(liste) || liste.OrderByDescending(x => x).ToList().SequenceEqual(liste))
    {
        for (int i = 0; i < liste.Count - 1; i++)
        {
            if (!(Math.Abs(liste[i] - liste[i + 1]) > 0 && Math.Abs(liste[i] - liste[i + 1]) < 4))
            {
                return false;
            }
        }
        return true;
    }
    else
    {
        return false;
    }
}

