    string pattern = @"do\(\)|don't\(\)|mul\(\d{1,3},\d{1,3}\)";

    // Instantiate the regular expression object.
    Regex r = new Regex(pattern, RegexOptions.IgnoreCase);

    // Match the regular expression pattern against a text string.
    Match m = r.Match(test);
    List<string> mult = new List<string>();
    while (m.Success)
    {
        mult.Add(m.Value);
        m = m.NextMatch();
    }

    var res = 0;

    var activated = true;
    foreach (var finded in mult)
    {
        if (finded.Equals("don't()"))
        {
            activated = false;
        }
        else if (finded.Equals("do()"))
        {
            activated = true;
        }
        else
        {
            if (activated)
