    string testT = "MMMSXXMASM\r\nMSAMXMSMSA\r\nAMXSXMAAMM\r\nMSAMASMSMX\r\nXMASAMXAMM\r\nXXAMMXXAMA\r\nSMSMSASXSS\r\nSAXAMASAAA\r\nMAMMMXMMMM\r\nMXMXAXMASX";
    var listGlobale = new List<List<string>>();

    var listH = testT.Split("\r\n").ToList();
    listGlobale.Add(listH);

    var listHInverse = Inverse(listH);
    listGlobale.Add(listHInverse);

    var listV = new List<string>();
    for (int i = 0; i < listH[0].Count(); i++)
    {
        var colonne = "";
        for (int j = 0; j < listH.Count(); j++)
        {
            colonne += listH[j][i];
        }
        listV.Add(colonne);
    }
    listGlobale.Add(listV);

    var listVInverse = Inverse(listV);
    listGlobale.Add(listVInverse);

    var listDDescendante = DiagSlash(listH);
    listGlobale.Add(listDDescendante);

    var listDDescendanteInverse = Inverse(listDDescendante);
    listGlobale.Add(listDDescendanteInverse);

    var listDMontante = DiagUnslash(listH);
    listGlobale.Add(listDMontante);

    var listDMontanteInverse = Inverse(listDMontante);
    listGlobale.Add(listDMontanteInverse);

    var result = 0;
    string pattern = @"XMAS";

    // Instantiate the regular expression object.
    Regex r = new Regex(pattern, RegexOptions.IgnoreCase);

    foreach (var list in listGlobale)
    {
        foreach (var item in list)
        {
            var number = 0;
            Match m = r.Match(item);
            while (m.Success)
            {
                number++;
                m = m.NextMatch();
            }
            result += number;
        }
    }

    return result;
}

