List<string> Inverse(List<string> listToInverse)
{
    var listInverse = new List<string>();
    for (int i = 0; i < listToInverse.Count(); i++)
    {
        var colonne = "";
        for (int j = listToInverse[i].Count() - 1; j >= 0; j--)
        {
            colonne += listToInverse[i][j];
        }
        listInverse.Add(colonne);
    }

    return listInverse;
}

List<string> DiagSlash(List<string> test)
{
    var result = new List<string>();

    for (int i = 0; i < test[0].Length; i++)
    {
        var x = i;
        var y = 0;

        string diag = "";

        while (x < test[0].Length && y < test.Count())
        {
            diag += test[y][x];
            x++;
            y++;
        }
        result.Add(diag);
    }

    for (int j = 1; j < test.Count(); j++)
    {
        var x = 0;
        var y = j;

        string diag = "";

        while (x < test[0].Length && y < test.Count())
        {
            diag += test[y][x];
            x++;
            y++;
        }
        result.Add(diag);
    }

    return result;
}

List<string> DiagUnslash(List<string> test)
{
    var result = new List<string>();

    for (int i = test[0].Length - 1; i >= 0 ; i--)
    {
        var x = i;
        var y = 0;

        string diag = "";

        while (x >= 0 && y < test.Count())
        {
            diag += test[y][x];
            x--;
            y++;
        }
        result.Add(diag);
    }

    for (int j = 1; j < test.Count(); j++)
    {
        var x = test[0].Length - 1;
        var y = j;

        string diag = "";

        while (x < test[0].Length && y < test.Count())
        {
            diag += test[y][x];
            x--;
            y++;
        }
        result.Add(diag);
    }
    return result;
}
